CREATE TABLE orders ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, order_date date, user_id BIGINT, status VARCHAR(255), CONSTRAINT pk_orders PRIMARY KEY (id) );
CREATE TABLE order_product ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, product_id BIGINT, quantity INTEGER, order_id BIGINT, CONSTRAINT pk_order_product PRIMARY KEY (id) );
ALTER TABLE order_product ADD CONSTRAINT FK_ORDER_PRODUCT_ON_ORDER FOREIGN KEY (order_id) REFERENCES orders (id);
